  å®ç° func findMin(nums []int) int { length := len(nums) left, right := 0, length - 1 for left &lt; right { mid := (left + right) / 2 if nums[mid] &gt; nums[right] { left = mid + 1 } else if nums[mid] &lt; nums[right] { right = mid } else { right-- } } return nums[right] }
